    private void jbSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbSalvarActionPerformed

        if (jrAtivo.isSelected()) {
            status = 1;
        }
        if (jrInativo.isSelected()) {
            status = 0;
        }
        int Cod;
        objpes = new clientes();
        preencher_objetosClientes();

        int CodF;
        objfic = new fichao();

        int CodP;
        objpre = new preco();

        //SALVAR NOVO CADASTRO
        try {
            IEDAO = new IncluirEditarDAO();

            if (acao == 1) {
                salvar();

                if (jtCodT1.getText().isEmpty()) {
                    //transportadora T1
                } else {
                    SalvarT1();
                    //Transportadora T1
                }

                if (jtCodT2.getText().isEmpty()) {
                    //Transportadora T2
                } else {
                    SalvarT2();
                    //Transportadora T2
                }

                if (jtCodT3.getText().isEmpty()) {
                    //Transportadora T3
                } else {
                    SalvarT3();
                    //Transportadora T3
                }

                Cod = IEDAO.IncluirClientes(objpes);
                jtCod.setText(String.valueOf(Cod));
                JOptionPane.showMessageDialog(this, "Salvo com sucesso !");

                try {

                    preencher_objetosFichao();

                    CodF = IEDAO.IncluirFichao(objfic);
                    jtCod2.setText(String.valueOf(CodF));
                    JOptionPane.showMessageDialog(this, "Fichão Gerado com sucesso !");

                    try {

                        preencher_objetoPreco();

                        CodP = IEDAO.IncluirPreco(objpre);
                        jtCodPreco.setText(String.valueOf(CodP));

                    } catch (SQLException error) {
                        
                    }

                } catch (SQLException error) {
                    cancelar();

                    JOptionPane.showMessageDialog(this, "Erro ao gerar fichão: " + error.getMessage());

                }

            }

        } catch (SQLException error) {
            /*if(error.getMessage() = Duplicate entry for key'cliente.Pes_cnpj_UNIQUE' ){
           }*/
            cancelar();

            JOptionPane.showMessageDialog(this, "Erro ao salvar: " + error.getMessage());

        }

        if (acao == 2) {

            preencher_objetosFichao();
            salvar();
            //transportadora T1
            SalvarT1();
            jbEditarT1.setEnabled(false);
            //Transportadora T1

            //Transportadora T2
            SalvarT2();
            jbEditarT2.setEnabled(false);
            //Transportadora T2

            //Transportadora T3
            SalvarT3();
            jbEditarT3.setEnabled(false);
            //Transportadora T3

            objpes.setId_clientes(Integer.parseInt(jtCod.getText()));

            objfic.setId_cliente(Integer.parseInt(jtCod.getText()));
            try {
                if (IEDAO.EditarClientes(objpes)) {

                    JOptionPane.showMessageDialog(this, "Alterado com Sucesso!");

                }

                if (IEDAO.EditarFichao(objfic)) {

                    JOptionPane.showMessageDialog(this, " Fichão Alterado!");

                }
            } catch (SQLException ex) {
                System.err.println("Erro: " + ex);
            }

        }


    }//GEN-LAST:event_jbSalvarActionPerformed

