    private void jbExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbExcluirActionPerformed

        if (jtCod.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Digite o Código! ");
        } else {
            int codigo = Integer.parseInt(jtCod.getText());

            EXDAO = new ExcluirDAO();

            try {
                if (JOptionPane.showConfirmDialog(this, "Confirmar Exclusão? ", "Confirmação",
                        JOptionPane.YES_NO_CANCEL_OPTION) == JOptionPane.YES_NO_OPTION) {

                    if (EXDAO.ExcluirPreco(codigo) && EXDAO.ExcluirFichaoCliente(codigo) && (EXDAO.ExcluirClientes(codigo))) {
                        JOptionPane.showMessageDialog(this, "Excluido com sucesso!");
                        cancelar();

                    } else {

                        JOptionPane.showMessageDialog(this, "Erro ao excluir o registro! ");

                    }
                } else {

                    JOptionPane.showMessageDialog(this, "Operação cancelada! ");
                    cancelar();

                }

            } catch (SQLException error) {
                JOptionPane.showMessageDialog(this, "Erro: Exclusão Não Realizada! " + error.getMessage());
            }
        }
    }//GEN-LAST:event_jbExcluirActionPerformed
    private void jbBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbBuscarActionPerformed

        objpes = new clientes();
        if (jtCod.getText().isEmpty()) {

            jifConsultaClientes objconsulta = new jifConsultaClientes();
            jPrincipal.jTela.add(objconsulta);
            objconsulta.setVisible(true);
            jPrincipal.jTela.setComponentZOrder(objconsulta, 0);

        } else {
            objpes.setId_clientes(Integer.parseInt(jtCod.getText()));

            PESDAO = new PesquisaDAO();

            try {

                if (PESDAO.PesquisarTelaClientes(objpes)) {
                    if ((objpes != null)) {
                        pesquisaClientes();

                    }
                }
            } catch (SQLException erro) {
                JOptionPane.showMessageDialog(this, "Erro ao pesquisar " + erro.getMessage());
            }
        }


    }//GEN-LAST:event_jbBuscarActionPerformed

