    private void jbSalvarT1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbSalvarT1ActionPerformed
        // TODO add your handling code here:
        if (jrAtivoT1.isSelected()) {
            status = 1;
        }
        if (jrInativoT1.isSelected()) {
            status = 0;
        }

        int Cod;
        objtra = new transportadora();
        preencher_objetosT1();

        try {

            IEDAO = new IncluirEditarDAO();

            if (acao == 1) {

                SalvarT1();

                Cod = IEDAO.IncluirTransportadora(objtra);
                jtCodT1.setText(String.valueOf(Cod));
                JOptionPane.showMessageDialog(this, "Salvo com sucesso !");
            }

        } catch (SQLException error) {
            JOptionPane.showMessageDialog(this, "Erro ao salvar: " + error.getMessage());
            CancelarT1();

        }

        if (acao == 2) {
            SalvarT1();

            objtra.setId_transportadora(Integer.parseInt(jtCodT1.getText()));

            try {
                if (IEDAO.EditarTransportadora(objtra)) {

                    JOptionPane.showMessageDialog(this, "Alterado com Sucesso!");

                }
            } catch (SQLException ex) {
                System.err.println("Erro: " + ex);
            }

        }

    }//GEN-LAST:event_jbSalvarT1ActionPerformed

