    private void jbSalvarT2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbSalvarT2ActionPerformed
        // TODO add your handling code here:
        if (jrAtivoT2.isSelected()) {
            status = 1;
        }
        if (jrInativoT2.isSelected()) {
            status = 0;
        }

        int Cod;
        objtra = new transportadora();
        preencher_objetosT2();

        try {

            IEDAO = new IncluirEditarDAO();

            if (acao == 1) {

                SalvarT2();

                Cod = IEDAO.IncluirTransportadora(objtra);
                jtCodT2.setText(String.valueOf(Cod));
                JOptionPane.showMessageDialog(this, "Salvo com sucesso !");
            }

        } catch (SQLException error) {
            JOptionPane.showMessageDialog(this, "Erro ao salvar: " + error.getMessage());
            CancelarT2();

        }

        if (acao == 2) {
            SalvarT2();

            objtra.setId_transportadora(Integer.parseInt(jtCodT2.getText()));

            try {
                if (IEDAO.EditarTransportadora(objtra)) {

                    JOptionPane.showMessageDialog(this, "Alterado com Sucesso!");

                }
            } catch (SQLException ex) {
                System.err.println("Erro: " + ex);
            }

        }
    }//GEN-LAST:event_jbSalvarT2ActionPerformed

