    private void jbSalvarT3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbSalvarT3ActionPerformed
        // TODO add your handling code here:
        if (jrAtivoT3.isSelected()) {
            status = 1;
        }
        if (jrInativoT3.isSelected()) {
            status = 0;
        }

        int Cod;
        objtra = new transportadora();
        preencher_objetosT3();

        try {

            IEDAO = new IncluirEditarDAO();

            if (acao == 1) {

                SalvarT3();

                Cod = IEDAO.IncluirTransportadora(objtra);
                jtCodT3.setText(String.valueOf(Cod));
                JOptionPane.showMessageDialog(this, "Salvo com sucesso !");
            }

        } catch (SQLException error) {
            JOptionPane.showMessageDialog(this, "Erro ao salvar: " + error.getMessage());
            CancelarT3();

        }

        if (acao == 2) {
            SalvarT3();

            objtra.setId_transportadora(Integer.parseInt(jtCodT3.getText()));

            try {
                if (IEDAO.EditarTransportadora(objtra)) {

                    JOptionPane.showMessageDialog(this, "Alterado com Sucesso!");

                }
            } catch (SQLException ex) {
                System.err.println("Erro: " + ex);
            }

        }
    }//GEN-LAST:event_jbSalvarT3ActionPerformed

