    public void buscarCepF(String cep) {

        try {
            URL url = new URL("http://viacep.com.br/ws/" + cep + "/xml");
            SAXReader xml = new SAXReader();
            Document documento = xml.read(url);
            Element root = documento.getRootElement();

            for (Iterator<Element> it = root.elementIterator(); it.hasNext();) {
                Element element = it.next();

                //if(jtRuaF.getText().isEmpty()){
                if (element.getQualifiedName().equals("logradouro")) {
                    jtRuaF.setText(element.getText().toUpperCase());
                    //}
                }
                // if (jtBairroF.getText().isEmpty()){
                if (element.getQualifiedName().equals("bairro")) {
                    jtBairroF.setText(element.getText().toUpperCase());
                    //}
                }
                //if (jtCidadeF.getText().isEmpty()){
                if (element.getQualifiedName().equals("localidade")) {
                    jtCidadeF.setText(element.getText().toUpperCase());
                    //}
                }
                if (element.getQualifiedName().equals("uf")) {
                    jcEstadoF.setSelectedItem(element.getText());
                }
                if (element.getQualifiedName().equals("ibge")) {
                    jtCodIbgeF.setText(element.getText());
                }

            }
        } catch (Exception e) {

            System.err.println("Erro: " + e);

        }
    }

