    public void buscarCNPJ() {
        String cnpj = jtCnpj.getText();
        cnpj = cnpj.replaceAll("[^0123456789]", "");
        try {
            PessoaJuridica pessoa = getController().consulta(cnpj);

            if (pessoa != null) {

                jcEstadoF.setSelectedItem(pessoa.getEstado().toUpperCase());
                jtRazaoS.setText(pessoa.getRazaoSocial().toUpperCase());
                jtBairroF.setText(pessoa.getBairro().toUpperCase());
                jtRuaF.setText(pessoa.getRua().toUpperCase());
                jtNumF.setText(pessoa.getNumero());
                jtTelF.setText(pessoa.getTelefone());
                jtCepF.setText(pessoa.getCep());
                jtComplF.setText(pessoa.getComplemento().toUpperCase());
                jtEmailCon.setText(pessoa.getEnderecoEletronico());
                jtCidadeF.setText(pessoa.getMunicipio().toUpperCase());

                if (pessoa.getSituacao().equals("ATIVA")) {
                    jrAtivo.setSelected(true);
                } else {
                    jrInativo.setSelected(true);
                }

            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "CNPJ n�o encontrado.");
        }
    }

